import React, { useState } from 'react';
// Import FontAwesome components and icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import '@fortawesome/fontawesome-free/css/all.min.css';

const Register = () => {
  const [input, setInput] = useState({
    username: "",
    email: "",
    password: ""
  });

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic for form submission
    console.log('Form submitted with data:', input);
    // Clear form input
    setInput({
      username: "",
      email: "",
      password: ""
    });
  };

  return (
    <div
      className="bg-cover bg-center h-screen"
      style={{ backgroundImage: "url('https://placehold.co/1920x1080?text=Background+Image')" }}
    >
      <div className="flex items-center justify-center h-full">
        <div className="bg-white bg-opacity-90 p-8 rounded-lg shadow-lg w-96">
          <h2 className="text-2xl font-bold mb-6 text-center">Register</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <div className="flex items-center border rounded-full px-3 py-2">
                {/* FontAwesome User icon */}
                <FontAwesomeIcon icon={faUser} className="text-blue-500" />
                <input
                  type="text"
                  name="username"
                  placeholder="Enter your username"
                  className="ml-2 w-full border-none focus:outline-none"
                  value={input.username}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="mb-4">
              <div className="flex items-center border rounded-full px-3 py-2">
                {/* FontAwesome Email icon */}
                <FontAwesomeIcon icon={faEnvelope} className="text-blue-500" />
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  className="ml-2 w-full border-none focus:outline-none"
                  value={input.email}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="mb-6">
              <div className="flex items-center border rounded-full px-3 py-2">
                {/* FontAwesome Lock icon */}
                <FontAwesomeIcon icon={faLock} className="text-blue-500" />
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  className="ml-2 w-full border-none focus:outline-none"
                  value={input.password}
                  onChange={handleChange}
                />
              </div>
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 text-white py-2 rounded-full hover:bg-blue-600"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
