import React, { useState } from 'react';
import './Register.css';
import { useNavigate } from 'react-router-dom';
// Import FontAwesome components and icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import '@fortawesome/fontawesome-free/css/all.min.css';

const Register = () => {
  const navigate = useNavigate(); // Initialize useNavigate hook
  const [input, setInput] = useState({
    name: "",
    email: "",
    password: ""
  });

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevents page refresh on form submission
    // Store user data in localStorage
    localStorage.setItem("user", JSON.stringify(input));
    
    // Navigate to the login page
    navigate("/login");
    
    console.log('Form submitted with data:', input);

    setInput({
      name: "",
      email: "",
      password: ""
    });
  };

  return (
    <div className="flex items-center justify-center h-screen bg-white">
      <div className="w-full max-w-xs">
        <h1 className="text-5xl font-light mb-2">Register</h1>

        <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
          
          {/* Username Input */}
          <div className="mb-4">
            <div className="flex items-center bg-gray-200 rounded">
              <span className="inline-block w-10 h-10 leading-10 text-center text-white bg-cyan-500 rounded-l">
                <FontAwesomeIcon icon={faUser} />
              </span>
              <input
                className="w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="name"
                value={input.name}
                onChange={handleChange}
                type="text"
                placeholder="Username"
                required
              />
            </div>
          </div>

          {/* Email Input */}
          <div className="mb-4">
            <div className="flex items-center bg-gray-200 rounded">
              <span className="inline-block w-10 h-10 leading-10 text-center text-white bg-cyan-500 rounded-l">
                <FontAwesomeIcon icon={faEnvelope} />
              </span>
              <input
                className="w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="email"
                value={input.email}
                onChange={handleChange}
                type="email"
                placeholder="Email"
                required
              />
            </div>
          </div>

          {/* Password Input */}
          <div className="mb-4">
            <div className="flex items-center bg-gray-200 rounded">
              <span className="inline-block w-10 h-10 leading-10 text-center text-white bg-cyan-500 rounded-l">
                <FontAwesomeIcon icon={faLock} />
              </span>
              <input
                className="w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="password"
                value={input.password}
                onChange={handleChange}
                type="password"
                placeholder="Password"
                required
              />
            </div>
          </div>

          {/* Submit Button */}
          <button type="submit" className="bg-cyan-500 hover:bg-cyan-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
